const FinallAvgGrade = ({ grade, isForTable = false }) => {

  //initialize styles
  const tableStyles = { display: "flex", justifyContent: "center" }
  const regularStyles = {
    display: "flex",
    flexDirection: "column",
    position: "absolute",
    right: "50%",
    transform: "translateX(50%)",
  }
  return (
    <div
      style={
        isForTable
          ? tableStyles
          : regularStyles
      }
    >
      <div
        style={isForTable ? {
          borderRadius: "90px",
          display : "flex",
          justifyContent : "center",
          alignItems : "center",
          color: "white",
          background: "#7A55B5",
          width: "40px",
          padding: "10px",
          boxShadow : "0 0 4px black",
          height : "40px",
          transform:"translateY(-25%)"
        } :{
          borderRadius: "50px",
          color: "#7A55B5",
          background: "white",
          width: "fit-content",
          padding: "15px",
          boxShadow: "0 0 3px #DED5ED",
        }}
      >
        {grade}
      </div>
    </div>
  );
};

export default FinallAvgGrade;
