import FinallAvgGrade from "../../components/js/finallAvgGrade";
import LeadingClassesCards from "../../components/js/leadingClasses";
import LeadersTable from "../../components/js/table";

const data = [
  {
    key: 1,
    place: 1,
    school: "John Brown",
    grade: "ו-2",
    city: "בת ים",
    howManyStudents: 23,
    avgGradePerAssignment: (
      <AvgGradePerAssigment
        avgGradePerAssigment={[
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
        ]}
      ></AvgGradePerAssigment>
    ),
    overallAvgGrade: <FinallAvgGrade grade={78}></FinallAvgGrade>,
  },
  {
    key: 2,
    place: 2,
    school: "John Brown",
    grade: "ו-4",
    city: "חיפה",
    howManyStudents: 43,
    avgGradePerAssignment: (
      <AvgGradePerAssigment
        avgGradePerAssigment={[
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
        ]}
      ></AvgGradePerAssigment>
    ),
    overallAvgGrade: <FinallAvgGrade grade={90}></FinallAvgGrade>,
  },
];

const grades = [
  { grade: 80, name: "maor" },
  { grade: 80, name: "maor" },
  { grade: 80, name: "maor" },
  { grade: 80, name: "maor" },
  { grade: 80, name: "maor" },
];

const FirstScreen = () => {
  return (
    <>
      <br></br>
      <div
        style={{
          width: "80%",
          margin: "auto",
          display: "grid",
          gridTemplateColumns: "auto auto auto",
          justifyContent: "space-evenly",
        }}
      >
        <LeadingClassesCards
          finallGrade={80}
          gradesPerAssigment={grades}
          city={"בת ים"}
          numberOfStudents={28}
          nameOfClass={"ו-2"}
          schoolName={"בית הספר אגמים"}
          gift={"5000 ₪"}
        ></LeadingClassesCards>
        <LeadingClassesCards
          finallGrade={80}
          gradesPerAssigment={grades}
          city={"בת ים"}
          numberOfStudents={28}
          nameOfClass={"ו-2"}
          schoolName={"בית הספר אגמים"}
          gift={"500 ₪"}
        ></LeadingClassesCards>
        <LeadingClassesCards
          finallGrade={80}
          gradesPerAssigment={grades}
          city={"בת ים"}
          numberOfStudents={28}
          nameOfClass={"ו-2"}
          schoolName={"בית הספר אגמים"}
          gift={"2342345 ₪"}
        ></LeadingClassesCards>
      </div>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <LeadersTable data={data}></LeadersTable>
    </>
  );
};

export default FirstScreen;
