import { useEffect, useState } from "react";
import "./"
const LeadersTable = ({data}) => {
const [dataInfo,setDataInfo] = useState([]);
const [searchWord,setSearchWord] = useState(null);


useEffect(()=>{
  setDataInfo(data);
},[data]);
  return (
    <div>
      <div style={{width:"80%",display:"flex",justifyContent:"start"}}>
        <input onFocus={()=> setSearchWord(null)} onBlur={(text)=> setSearchWord(text.target.value)} value={searchWord ?? searchWord} style={{borderRadius:"10px",border:"1px solid black",boxShadow:"0 0 8px #DED5ED",width:"20%"}} placeholder="sdsdfsdf" type={"text"} />
        <button onClick={()=>{
          setSearchWord(null);
        }}>נקה חיפוש</button>
      </div>
      <table style={{width : "80%",margin: "50px auto",borderRadius:"10px",boxShadow:"0 0 8px #DED5ED",borderStyle:"hidden"}}>
              <tr id="table-head">
                  <td>ציון ממוצע סופי</td>
                  <td>ציון ממוצע למטלה</td>
                  <td>עיר</td>
                  <td>תלמידים</td>
                  <td>כיתה</td>
                  <td>בית ספר</td>
                  <td>מקום</td>
              </tr>
          <tbody>
            {dataInfo.map(row => {
                return (
                    <tr>
                    <td>{row.overallAvgGrade}</td>
                    <td>{row.avgGradePerAssignment}</td>
                    <td>{row.city}</td>
                    <td>{row.howManyStudents}</td>
                    <td>{row.grade}</td>
                    <td>{row.school}</td>
                    <td>{row.place}</td>
                </tr>
                );
            })}
          </tbody>
      </table>
    </div>
  );
};

export default LeadersTable;
