import AvgGradePerAssigment from "./avgGradePerAssigment";
const LeadingClasses = ({
  schoolName,
  gift,
  nameOfClass,
  numberOfStudents,
}) => {
  return (
    <div style={{ width: "30%", margin: "auto",position:"relative" }}>
      <h3>{gift}</h3>
      <div
        style={{
          borderRadius: "25px",
          boxShadow: "0 0 4px grey",
          padding: "25px",
        }}
      >
        <h3 style={{ textAlign: "end" }}>{schoolName}</h3>
        <div style={{display:"grid",gridTemplateColumns:"auto auto", width:"50%",position:"absolute",right:"3px"}}>
          <h5 style={{textAlign:"end"}}>{numberOfStudents} תלמידים</h5>
            <div>

            </div>
          <h5 style={{textAlign:"end"}}>{nameOfClass}</h5>
        </div>
        <h4 style={{ textAlign: "center" }}>ציון ממוצע למטלה</h4>
        <AvgGradePerAssigment
          avgGradePerAssigment={[
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
          ]}
        ></AvgGradePerAssigment>
      </div>
    </div>
  );
};

export default LeadingClasses;
