import "../css/avgGradePerAssigment.css";

const AssigmentsBadge = ({ isForMobilsTable, assigment, index }) => {
  return (
    <div className="avg-grade-per-assigment-center">
      <div className={isForMobilsTable ? "avg-grade-per-assigment" : "avg-grade-per-assigment-mobile"}>
        {assigment.classAverage && assigment.classAverage}
      </div>
      <h4 style={{ margin: "auto" }}># {index && index}</h4>
    </div>
  );
};
const AvgGradePerAssigment = ({ isForMobilsTable ,lessons, ...htmlAttributes }) => {
  return (
    <div className="avg-grade-per-assigment-wrraper" {...htmlAttributes}>
      {lessons.map((assigment, index) => {
        return (
          <AssigmentsBadge isForMobilsTable={true} assigment={assigment} index={index} key={index} />
        );
      })}
    </div>
  );
};

export default AvgGradePerAssigment;
export { AssigmentsBadge };
