import "../css/avgGradePerAssigment.css";

const AvgGradePerAssigment = ({avgGradePerAssigment}) => {
    return (
        <div
        className="avg-grade-per-assigment-wrraper"

      >
        {avgGradePerAssigment.map((assigment,index) => {
          return (
            <div style={{ display: "flex", flexDirection: "column",margin:"10px" }} key={index}>
              <div
                style={{
                  borderRadius: "90px",
                  display : "flex",
                  justifyContent : "center",
                  alignItems : "center",
                  color: "white",
                  background: "#7A55B5",
                  width: "40px",
                  padding: "10px",
                  boxShadow : "0 0 4px black",
                  height : "40px",
                }}
              >
                {assigment.grade}
              </div>
              {assigment.name}
            </div>
          );
        })}
      </div>
    )
}

export default AvgGradePerAssigment;
