import AvgGradePerAssigment from "../../components/js/avgGradePerAssigment";
import FinallAvgGrade from "../../components/js/finallAvgGrade";
import LeadingClasses from "../../components/js/leadingClasses";
import LeadersTable from "../../components/js/table";
import useWindowDimensions from "../../helpers/screenDimentions";

import "../css/firstScreen.css";
// const isMobile = true;

//dummy data
const tableDummyData = [
  {
    key: 1,
    schoolName: "John Brown",
    name : "ו-2",
    studentsCount: 23,
    lessons: (
      <AvgGradePerAssigment
        avgGradePerAssigment={[
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
          { grade: 80, name: "maor" },
        ]}
      ></AvgGradePerAssigment>
    ),
    average: <FinallAvgGrade isForTable grade={78}></FinallAvgGrade>,
  }
];

const dummyColumnsMobile = [
  "מטלות",
  "ציון סופי",
  "תלמידים",
  "כיתה",
  "בית ספר",
  "מקום",
];

const dummyColumns = [
  "ציון ממוצע סופי",
  "ציון ממוצע למטלה",
  "תלמידים",
  "כיתה",
  "בית ספר",
  "מקום",
];


const cardsDummyData = [
  {
    average: 80,
    gradesPerAssigment: [
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
    ],
    numberOfStudents: 28,
    name: "כיתה ו 1 קרית גת",
    schoolName: "בית הספר אגמים",
    gift: "אוזניות לכל הכיתה ",
  },
  {
    average: 80,
    gradesPerAssigment: [
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
      { grade: 80, name: "maor" },
    ],
    numberOfStudents: 28,
    name: "כיתה ו 1 קרית גת",
    schoolName: "בית הספר אגמים",
    gift: "אוזניות לכל הכיתה ",
  },
  {
    average: 80,
    gradesPerAssigment: [
      {
        classAverage: 74,
        finished: 1,
        score: 7400,
      },
      {
        "classAverage": 74,
        "finished": 1,
        "score": 7400
    },
    ],
    numberOfStudents: 28,
    name: "כיתה ו 1 קרית גת",
    schoolName: "בית הספר אגמים",
    gift: "אוזניות לכל הכיתה ",
  },
];

const FirstScreen = () => {
  const { width } = useWindowDimensions();

  return (
    <>
      <br></br>

      <div style={{ display: "flex", justifyContent: "center" }}>
        <button
          className="all-schools-list-button"
          onClick={() => {
            window.location.href = "/#table";
          }}
        >
          :כל בתי הספר המשתתפים
        </button>
      </div>

      <LeadingClasses classes={cardsDummyData} />
      <br></br>
      <br></br>
      <LeadersTable
        colums={width > 900 ? dummyColumnsMobile : dummyColumns}
        data={tableDummyData}
      ></LeadersTable>
      <br></br>
      <br></br>
    </>
  );
};

export default FirstScreen;
