const LeadingClasses = ({ schoolName, gift, avgGradePerAssigment }) => {
  return (
    <div style={{ width: "30%", margin: "auto" }}>
      <h3>{gift}</h3>
      <div style={{ borderRadius: "25px", boxShadow: "0 0 4px grey" }}>
        <h5>{schoolName}</h5>
      </div>
      <div style={{display:"grid",gridTemplateColumns : "auto auto auto auto auto"}}>
        {avgGradePerAssigment.map((assigment) => {
          return (
            <div style={{display:"flex",flexDirection:"column"}}>
              <div
                style={{
                  borderRadius: "90px",
                  color: "white",
                  background: "#7A55B5",
                  width: "fit-content",
                  padding: "10px",
                }}
              >
                {assigment.grade}
              </div>
              {assigment.name}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default LeadingClasses;
