import AvgGradePerAssigment from "./avgGradePerAssigment";
import "../css/LeadingClassesCards.css"
import FinallAvgGrade from "./finallAvgGrade";
const LeadingClasses = ({
  schoolName,
  gift,
  nameOfClass,
  numberOfStudents,
  city,
  finallGrade,
  gradesPerAssigment
}) => {
  return (
    <div className="card-wrrapper">
      <h3>{gift}</h3>
      <div className="cards-main-div">
        <h3>{schoolName}</h3>
        <div className="school-data-div">
          <h5>{numberOfStudents} תלמידים</h5>
          <div>
            <h5 >{nameOfClass}</h5>
            <h5 >{city}</h5>
          </div>
        </div>

        <br></br>
        <br></br>
        <h4 style={{ textAlign: "center" }}>ציון ממוצע למטלה</h4>
        <AvgGradePerAssigment
          avgGradePerAssigment={gradesPerAssigment}
        ></AvgGradePerAssigment>
        <br></br>
          <h5>ציון סופי ממוצע</h5>
          <FinallAvgGrade grade={finallGrade}></FinallAvgGrade>
      </div>
    </div>
  );
};


const OK = ({classes}) => {
    return (
        <>
        {classes.map(item => {
            return                 <LeadingClasses
            finallGrade={80}
            gradesPerAssigment={grades}
            city={"בת ים"}
            numberOfStudents={28}
            nameOfClass={"ו-2"}
            schoolName={"בית הספר אגמים"}
            gift={"5000 ₪"}
          ></LeadingClasses>
        })}
        </>
    );
}

export default LeadingClasses;
