import { useEffect, useState } from "react";
import "../css/table.css";

const LeadersTable = ({ data }) => {
  const [dataInfo, setDataInfo] = useState([]);
  const [searchWord, setSearchWord] = useState(null);

  useEffect(() => {
    setDataInfo(data);
  }, [data]);

  const onFinish = (text) => {
    setSearchWord(text.target.value);
    const filteredData = dataInfo.filter(
      (item) => item.schoolName === text.target.value
    );
    console.log(filteredData);
    if (text.target.value !== "") {
      setDataInfo(filteredData);
    }
  };

  return (
    <div style={{ width: "60%", margin: "auto" }}>
      <div id="search-wrapper">
        <input
          onFocus={() => setSearchWord(null)}
          onBlur={onFinish}
          id="search-input"
          placeholder="sdsdfsdf"
          type={"text"}
        />
        <button
          onClick={() => {
            setSearchWord(null);
          }}
        >
          נקה חיפוש
        </button>
        <h3></h3>
      </div>
      <table id="table">
        <thead id="table-head">
          <tr>
            <td>ציון ממוצע סופי</td>
            <td>ציון ממוצע למטלה</td>
            <td>עיר</td>
            <td>תלמידים</td>
            <td>כיתה</td>
            <td>בית ספר</td>
            <td>מקום</td>
          </tr>
        </thead>
        <tbody>
          {dataInfo.map((row,index) => {
            return (
              <tr key={index}>
                <td>{row.overallAvgGrade}</td>
                <td>{row.avgGradePerAssignment}</td>
                <td>{row.city}</td>
                <td>{row.howManyStudents}</td>
                <td>{row.grade}</td>
                <td>{row.school}</td>
                <td>{row.place}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default LeadersTable;
