import AvgGradePerAssigment from "./avgGradePerAssigment";
const LeadingClasses = ({
  schoolName,
  gift,
  nameOfClass,
  numberOfStudents,
}) => {
  return (
    <div style={{ width: "30%", margin: "auto" }}>
      <h3>{gift}</h3>
      <div
        style={{
          borderRadius: "25px",
          boxShadow: "0 0 4px grey",
          padding: "25px",
        }}
      >
        <h3 style={{ textAlign: "end" }}>{schoolName}</h3>
        <div style={{display:"grid",gridTemplateColumns:"auto auto"}}>
          <h5 style={{}}>{numberOfStudents}</h5>
          <h5>{nameOfClass}</h5>
        </div>
        <h4 style={{ textAlign: "center" }}>ציון ממוצע למטלה</h4>
        <AvgGradePerAssigment
          avgGradePerAssigment={[
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
          ]}
        ></AvgGradePerAssigment>
      </div>
    </div>
  );
};

export default LeadingClasses;
