import AvgGradePerAssigment from "./avgGradePerAssigment";
import "../css/LeadingClassesCards.css";
import FinallAvgGrade from "./finallAvgGrade";



const LeadingClassesCards = ({
  schoolName,
  gift,
  nameOfClass,
  numberOfStudents,
  finallGrade,
  gradesPerAssigment,
}) => {
  return (
    <div className="card-wrrapper">
      <h3>{gift}</h3>
      <div className="cards-main-div">
        <h3>{schoolName}</h3>
        <div className="school-data-div">
            <h5>{nameOfClass}</h5>
          <h5>תלמידים {numberOfStudents}</h5>
        </div>
        <h4 style={{ textAlign: "center" }}>ציון ממוצע למטלה</h4>
        <AvgGradePerAssigment
          avgGradePerAssigment={gradesPerAssigment}
        ></AvgGradePerAssigment>
        <br></br>
        <h5 style={{textAlign:"center"}}>ציון סופי ממוצע</h5>
        <FinallAvgGrade grade={finallGrade}></FinallAvgGrade>
      </div>
    </div>
  );
};




const LeadingClasses = ({ classes }) => {
  return (
    <div
      style={{
        width: "80%",
        margin: "auto",
        display: "grid",
        gridTemplateColumns: "auto auto auto",
        justifyContent: "space-evenly",
      }}
    >
      {classes.map((item) => {
        return (
          <LeadingClassesCards
            finallGrade={item.finallGrade}
            gradesPerAssigment={item.gradesPerAssigment}
            numberOfStudents={item.numberOfStudents}
            nameOfClass={item.nameOfClass}
            schoolName={item.schoolName}
            gift={item.gift}
          ></LeadingClassesCards>
        );
      })}
    </div>
  );
};

export default LeadingClasses;
