import AvgGradePerAssigment from "./avgGradePerAssigment";
import "../css/LeadingClassesCards.css"
const LeadingClasses = ({
  schoolName,
  gift,
  nameOfClass,
  numberOfStudents,
  city
}) => {
  return (
    <div className="card-wrrapper">
      <h3>{gift}</h3>
      <div className="cards-main-div">
        <h3>{schoolName}</h3>
        <div className="school-data-div">
          <h5>{numberOfStudents} תלמידים</h5>
          <div>
            <h5 >{nameOfClass}</h5>
            <h5 style={{ textAlign: "end" }}>{city}</h5>
          </div>
        </div>

        <br></br>
        <br></br>
        <h4 style={{ textAlign: "center" }}>ציון ממוצע למטלה</h4>
        <AvgGradePerAssigment
          avgGradePerAssigment={[
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
            { grade: 80, name: "maor" },
          ]}
        ></AvgGradePerAssigment>
      </div>
    </div>
  );
};

export default LeadingClasses;
